package Day1.maths;

public class exp1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int result1 = Math.max(51, 29);
		System.out.println(result1);
		
		int result2 = Math.min(21, 51);
		System.out.println(result2);
		
		double result3 = Math.sqrt(64);
		System.out.println(result3);
		
		double result4 = Math.abs(-4.77);
		System.out.println(result4);
		
		double result5 = Math.random();
		System.out.println(result5);
		
		int result6 = (int)(Math.random() * 1001);
		System.out.println(result6);

		int res = Math.addExact(10, 20);
		System.out.println(res);
		
		double ress = Math.pow(2, 2);
		System.out.println(ress);
	}

}
